class Tweet < ActiveRecord::Base

  belongs_to :user, :class_name => 'TwitterAccount'
  has_many :translations, :class_name => 'TweetTranslation'

  #attr_accessor :text
  attr_reader :translation #, #:id, :from_user

  before_validation :truncate_text_for_postgres

