  private
  def self.new_from_hash(hash)
    tweet = Tweet.new
    #@from_user = hash['from_user']
    #@to_user_id = hash['to_user_id']
    tweet.text = hash['text']
    tweet.twitter_id = hash['id']

    tweet.irt_screen_name = hash['in_reply_to_screen_name']
    tweet.irt_user_id = hash['in_reply_to_user_id']
    tweet.irt_status_id = hash['in_reply_to_status_id']
    tweet.contributors = hash['contributors']
    tweet.source = hash['source']
    tweet.tw_created_at = hash['created_at']
    tweet
  end
=begin
  public
  def translation=(value)
    @translation = value
    repair_translation!
  end

  def to_s
    "Original:    #{@text}\n" +
    "Translation: #{@translation}"
  end
=end

  public
  def url
    "http://twitter.com/#{self.user.username}/status/#{self.twitter_id}"
  end

  def translated
    return '' if self.translations.nil? || self.translations.count == 0
    self.translations.each do |translation|
      return translation.text unless translation.text.nil? || translation.text.empty?
    end
    ''
  end

  def truncate_text_for_postgres
    self.text = self.text[0,255]
  end

  def store_translation(translation, service_id)
    self.translations << TweetTranslation.new(:service_id => service_id, :text => translation)
    self.save
  rescue Exception => e
    Rails.logger.error "#{e.message}\nTweet: #{self.url}\n" +
      "Translation by #{Service.find(service_id).name}: #{translation}\n#{e.backtrace.join("\n")}"
  end

  def self.raw_translation_xml(service_symbol)
    service = Service.find_by_symbol(service_symbol)
    raise Service::NotFound unless service

    #  select all tweets, that have no translation by the given service
    tweet_ar = Tweet.all.select do |tweet|
      tweet.translations.detect{|translation|
        translation.service_id == service.id
      }.nil?
    end

    tweet_ar.map{|tweet| {'id' => tweet.twitter_id, 'text' => tweet.text}}.to_xml
  end
end

